import { useState } from "react";
import "./../styles/App.scss";
import Button from "./Button";

interface IMenuItemsProps {
  item: any;
}

function MenuItems({ item }: IMenuItemsProps) {
  console.log(item);
  const [showMenuItem, setShowMenuItem] = useState<number | undefined>();

  const { title, color, desc, cta1, cta2 } = item.attributes.element;
  console.log(showMenuItem, item);
  return (
    <div className="menu-items-container">
      <div
        className="menu-button"
        style={{ borderTop: `2px solid ${color}` }}
        onMouseEnter={() => setShowMenuItem(item.id)}
        onMouseLeave={() => setShowMenuItem(undefined)}
      >
        <h2>{title}</h2>
      </div>
      {/* {showMenuItem === item.id ? ( */}
      <div className="menu-content" style={{ backgroundColor: color }}>
        <div className="left-part">
          <p>{title}</p>
          <span>{desc}</span>
          <div>
            <Button color="#2d5481" label={cta1} />
            <Button color="white" textColor="#2d5481" label={cta2} />
          </div>
        </div>
      </div>
      {/* ) : null} */}
    </div>
  );
}

export default MenuItems;
