import { useEffect, useState } from "react";
import "./../styles/App.scss";
import { getLocaleAvailable } from "../api/apiCall";

interface ISelectLanguageProps {
  language: string;
  setLanguage: (locale: string) => void;
}

function SelectLanguage({ setLanguage, language }: ISelectLanguageProps) {
  const [isDropDownOpen, setIsDropDownOpen] = useState(false);
  useEffect(() => {
    const fetchAvailableLanguage = async () => {
      let resultLocales = await getLocaleAvailable();
      setLanguage(resultLocales);
    };
    fetchAvailableLanguage();
  }, [language]);
  return (
    <>
      <button
        onClick={() => setIsDropDownOpen(!isDropDownOpen)}
        className="button"
      >
        {language}
      </button>
      {isDropDownOpen ? (
        <ul className="dropdown-language">
          <li onClick={() => setLanguage("FR")}>French</li>
          <li onClick={() => setLanguage("EN")}>English</li>
        </ul>
      ) : null}
    </>
  );
}

export default SelectLanguage;
